import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css'; 
import 'slick-carousel/slick/slick-theme.css';
import './Slider.css'; // Custom styling

// Import images
import slider1 from './slider1.webp';
import slider2 from './slider2.webp';
import slider3 from './slider3.webp';
import slider4 from './slider4.webp';

const ZoomImageSlider = () => {
  const [zoomedIndex, setZoomedIndex] = useState(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      setZoomedIndex(0); 
    }, 200); 

    return () => clearTimeout(timer);
  }, []);

  const handleAfterChange = (current) => {
    setZoomedIndex(current); 
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    afterChange: handleAfterChange,
    centerMode: true, 
    centerPadding: '0', 
    arrows: false, 
  };

  const images = [slider1, slider2, slider3, slider4];

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index} className={`zoom-effect ${index === zoomedIndex ? 'zoomed' : ''}`}>
            <img src={image} alt={`slide-${index}`} className="slider-image" />
          </div>
        ))}
      </Slider>
      <div className="mainbottomcontainer">
        <div className="bottomcontent">
          <p style={{fontSize:'20px'}}>For the Bold, Stylish & Confident you</p>
          <p>Our collection features a range of stylish and versatile outfits perfect for any casual occasion. From breezy kurtas to comfy palazzos, each piece is crafted with care and attention to detail. We use high-quality fabrics that are breathable and easy to care for, ensuring that you look and feel your best all day long.</p>
        </div>
      </div>
    </div>
  );
};

export default ZoomImageSlider;
