import React, { useState } from 'react';
import "./Songs.css";
import { Link } from 'react-router-dom';

import badshah from './singerimage/bd.jpg';
import hony from './singerimage/hs.jpg';
import alka from './singerimage/alka.jpg';
import neha from './singerimage/neha.jpg';
import am from './singerimage/am.jpg';
import jack from './singerimage/jack.jpg';
import kish from './singerimage/kish.jpg';
import sonu from './singerimage/sonu.jpg';
import udit from './singerimage/udit.jpg';
import arjit from './singerimage/arjit.jpg';

const songcollection = [
  { id: 'song1', sname: 'Badshah', name: 'abcd.mp3', description: 'this is song no 1', songcover: badshah },
  { id: 'song2', sname: 'Hony singh', name: 'abcd.mp3', description: 'this is song no 2', songcover: hony },
  { id: 'song3', sname: 'Alka', name: 'abcd.mp3', description: 'this is song no 3', songcover: alka },
  { id: 'song4', sname: 'Ami banta', name: 'abcd.mp3', description: 'this is song no 4', songcover: am },
  { id: 'song5', sname: 'Neha', name: 'abcd.mp3', description: 'this is song no 5', songcover: neha },
  { id: 'song6', sname: 'Jack', name: 'abcd.mp3', description: 'this is song no 6', songcover: jack },
  { id: 'song7', sname: 'Kishore', name: 'abcd.mp3', description: 'this is song no 7', songcover: kish },
  { id: 'song8', sname: 'Kishore', name: 'abcd.mp3', description: 'this is song no 7', songcover: kish },
  { id: 'song9', sname: 'Kishore', name: 'abcd.mp3', description: 'this is song no 7', songcover: kish },
  // Add more items if needed
];

const Song = () => {
    const [statusArray, setStatusArray] = useState(Array(songcollection.length).fill('add'));

    const addsinger = (index) => {
      setStatusArray((prevStatusArray) =>
        prevStatusArray.map((status, i) =>
          i === index ? (status === 'add' ? 'tick' : status === 'tick' ? 'cross' : 'add') : status
    )
  );
};

  return (
    <div style={{overflowX: 'auto',whiteSpace: 'nowrap',padding: '10px',display: 'flex',alignItems: 'flex-start',scrollbarWidth: 'none',}}>
      {songcollection.map((data, index) => (
        <div key={data.id} style={{  display: 'inline-block',  textAlign: 'center',  borderRadius: '8px',  overflow: 'hidden',  boxShadow: '0 4px 8px rgba(0,0,0,0.2)',  cursor: 'pointer',  transition: 'transform 0.3s, box-shadow 0.3s',  marginRight: '15px',  width: '160px',  height: 'auto', }}onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}>
          <Link to="/MusicPlayer">
            <img
src={data.songcover}
 alt="image not found here sorry"
style={{
width: '100%', // Fill the width of the container
height: '160px', // Fixed height for all images
objectFit: 'cover',
borderRadius: '6px',
display: 'block',
transition: 'opacity 0.3s',
}}/>
          </Link>
          <p style={{
            margin: '10px 0',
            fontSize: '16px',
            fontWeight: 'bold',
            color: '#333',
          }}>
            {data.sname}
          </p>
          <div style={{ marginTop: '10px' }}>
            <button
              onClick={() => addsinger(index)}
              style={{
                margin: '5px',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: statusArray[index] === 'add' ? '#f0f0f0' : statusArray[index] === 'tick' ? '#d4edda' : '#f8d7da',
                color: statusArray[index] === 'add' ? '#000' : statusArray[index] === 'tick' ? '#155724' : '#721c24',
                cursor: 'pointer',
                fontSize: '14px',
                transition: 'background-color 0.3s, color 0.3s',
              }}
              onMouseEnter={(e) => e.currentTarget.style.backgroundColor = statusArray[index] === 'add' ? '#e0e0e0' : statusArray[index] === 'tick' ? '#c3e6cb' : '#f5c6cb'}
              onMouseLeave={(e) => e.currentTarget.style.backgroundColor = statusArray[index] === 'add' ? '#f0f0f0' : statusArray[index] === 'tick' ? '#d4edda' : '#f8d7da'}
            >
              {statusArray[index] === 'add' && 'Add'}
              {statusArray[index] === 'tick' && '✔'}
              {statusArray[index] === 'cross' && '✖'}
            </button>
            <button
              style={{
                margin: '5px',
                padding: '10px 20px',
                border: 'none',
                borderRadius: '4px',
                backgroundColor: '#e7f0ff',
                color: '#007bff',
                cursor: 'pointer',
                fontSize: '14px',
                transition: 'background-color 0.3s, color 0.3s',
              }}
              onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#d0e3ff'}
              onMouseLeave={(e) => e.currentTarget.style.backgroundColor = '#e7f0ff'}
            >
              Learn
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Song;
