import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'; // Import Link for routing
import ImageSlider from './Slider';
import './Navbar.css';

const App = () => {
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isSearchVisible, setIsSearchVisible] = useState(false);
  const searchRef = useRef(null); // Reference to the search container

  const toggleTheme = () => {
    setIsLightTheme(!isLightTheme);
    document.body.classList.toggle('light-theme', !isLightTheme);
  };

  const handleSearchClick = () => {
    setIsSearchVisible(true);
  };

  const handleCloseSearch = () => {
    setIsSearchVisible(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <>
      <div className="topprice" style={{ textAlign: 'center' }}>
        <p style={{ fontSize: '40px' }}>WENLI</p>
      </div>

      <nav className={`navbar ${isLightTheme ? 'light-theme' : ''}`}>
        {!isSearchVisible ? (
          <>
            <div className="logo"></div>
            <ul className="nav-links">
              <li><Link to="/contact">Contact us</Link></li>
              <li><Link to="/">Home</Link></li>
              <li><Link to="/menswear">Mens wear</Link></li>
              <li><Link to="/womenswear">Womens wear</Link></li>
              <li><Link to="/track-order">Track Order</Link></li>
              <li><Link to="/store-locator">Store Locator</Link></li>
              <li><Link to="/sales">Sales</Link></li>
            </ul>
            <div className="nav-icons">
              <span onClick={handleSearchClick} className="search-icon">üîç</span>
              <span>üë§</span>
              <span>üõí</span>
              <button className="theme-switcher" onClick={toggleTheme}>
                {isLightTheme ? 'üåô' : 'üåû'}
              </button>
            </div>
          </>
        ) : (
          <div ref={searchRef} className={`search-container ${isSearchVisible ? 'show' : ''}`}>
            <input type="text" placeholder="Search..." className="search-input" />
            <button className="search-close" onClick={handleCloseSearch}>‚ùå</button>
          </div>
        )}
      </nav>


      <ImageSlider/>
    </>
  );
};

export default App;
