import React, { useEffect, useState, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './Completeview.css';
import ImageSlider from './Slider';

import LocalMallIcon from '@mui/icons-material/LocalMall';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import { IconButton, Button, InputBase, AppBar, Toolbar, Typography, Box, Drawer, List, ListItem, ListItemIcon, ListItemText, Popover, Paper, Divider, Avatar } from '@mui/material';
import { Rating } from '@mui/material';

import size from './images/a1.webp';
import CustomerReviews from './CustomerReviews';
import ProductGrid from './ProductGrid';




const CompleteView = () => {
  const location = useLocation();
  const { product } = location.state;
  const [initialLoad, setInitialLoad] = useState(true);

  useEffect(() => {
    window.scrollTo(0, 0);
    const timer = setTimeout(() => {
      setInitialLoad(false);
    }, 1000); 

    return () => clearTimeout(timer);
  }, []);

  const [popupOpen, setPopupOpen] = useState(false);
  const popupRef = useRef(null);

  const handleClickOutside = (event) => {
    if (popupRef.current && !popupRef.current.contains(event.target)) {
      setPopupOpen(false);
    }
  };

  useEffect(() => {
    if (popupOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [popupOpen]);

  return (
    <>
      <div className="complete-view">
        <div className="frames">
          <div className="frame">
            <img
              src={product.imgSrc}
              alt={product.title}
              className={initialLoad ? 'initial-zoom' : ''}
            />
          </div>

          <div className="frame">
            <img
              src={product.altImgSrc || product.imgSrc}
              alt={product.title}
              className={initialLoad ? 'initial-zoom' : ''}
            />
          </div>

          <div className="frame">
            <img
              src={product.imgSrc}
              alt={product.title}
              className={initialLoad ? 'initial-zoom' : ''}
            />
          </div>

          <div className="frame">
            <img
              src={product.altImgSrc || product.imgSrc}
              alt={product.title}
              className={initialLoad ? 'initial-zoom' : ''}
            />
          </div>
        </div>

        <div className="right-side">
          <h2 style={{ marginTop: '-100px' }}>{product.title}</h2>
          <div className="product-details">
            <p>{product.price}</p>
            <Button variant="contained" sx={{ mt: 1, width: '200px' }}>Review {'⭐'.repeat(product.rating)}</Button>
            <div className="rating"></div>
            <span style={{ fontSize: '20px', color: 'green' }}>Sizes</span><br />
            <div className="sizes">
              {product.sizes.map(size => (
                <span key={size} className="size">{size}</span>
              ))}
              <button style={{ fontSize: '20px', color: 'white', backgroundColor: 'black', borderRadius: '3px' }} onClick={() => setPopupOpen(true)}>Helps</button>
            </div>
            {popupOpen && (
              <div ref={popupRef} className="popup-overlay">
                <Paper className="popup-content">
                  <img src={size} alt="" className="zoom-image" />
                </Paper>
              </div>
            )}

        <div className="product-info">
          <h1>Jenny’s Closets – The winter top for female, green</h1>
          <div className="reviews">
            <span>⭐⭐⭐⭐⭐</span>
            <span>157 Reviews</span>
          </div>
          <div className="price">
            <span className="current-price">$49</span>
            <span className="original-price">$99</span>
            <span className="discount">Save 50% right now</span>
          </div>
          <div className="features">
            <h4>Features</h4>
            <ul>
              <li>Made with full cotton</li>
              <li>Slim fit for any body</li>
              <li>Quality control by JC</li>
            </ul>
          </div>


          <div className="colors">
            <h4>Colors</h4>
            <div className="color-options">
              <button className="color green"></button>
              <button className="color orange"></button>
              <button className="color blue"></button>
              <button className="color black"></button>
            </div>
          </div>

          <div className="Size-option">
            <h4>Size</h4>
            <div className="Size-options">
              <button className="M">S</button>
              <button className="M">M</button>
              <button className="X">X</button>
              <button className="XL">XL</button>
              <button className="XXL">XXL</button>
              <button className="XXL">HP</button>
            </div>
          </div>



            <div className="action-buttons">
              <button className="add-to-cart">Buy Now</button>
              <button className="add-to-cart">Add to cart</button>
              <button className="wishlist">
                <span> ♡ </span>
              </button>
            </div>
          </div>
        </div>
        </div>
      </div>
    </>
  );
};

export default CompleteView;
