import React, { useState, useRef } from 'react';
import './CreateContent.css';

const CreateContent = () => {
  const [video, setVideo] = useState(null);
  const [thumbnail, setThumbnail] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [links, setLinks] = useState([]);
  const [productImages, setProductImages] = useState([]);
  const [socialMediaIcons, setSocialMediaIcons] = useState([]);

  const videoInputRef = useRef(null);
  const thumbnailInputRef = useRef(null);
  const productImageInputRef = useRef(null);
  const socialMediaInputRef = useRef(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();

    if (video) formData.append('video', video);
    if (thumbnail) formData.append('thumbnail', thumbnail);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('links', JSON.stringify(links));

    productImages.forEach((file) => {
      formData.append('product_images', file);
    });

    socialMediaIcons.forEach((file) => {
      formData.append('social_media_icons', file);
    });

    try {
      const response = await fetch('http://localhost:8000/upload-media', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Server response:', errorText);
        throw new Error('Error uploading media');
      }

      const result = await response.json();
      alert('Media uploaded successfully!');
      // Reset form state
      setVideo(null);
      setThumbnail(null);
      setTitle('');
      setDescription('');
      setLinks([]);
      setProductImages([]);
      setSocialMediaIcons([]);
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while uploading media.');
    }
  };

  const addLink = () => {
    setLinks([...links, '']);
  };

  const handleLinkChange = (index, value) => {
    const newLinks = [...links];
    newLinks[index] = value;
    setLinks(newLinks);
  };

  const handleProductImageChange = (e) => {
    setProductImages([...e.target.files]);
  };

  const handleSocialMediaChange = (e) => {
    setSocialMediaIcons([...e.target.files]);
  };

  return (
    <div style={styles.container}>
      <form onSubmit={handleSubmit} style={styles.form}>
        <button type="button" onClick={() => videoInputRef.current.click()} style={styles.customButton}> Select Your Video</button>
        <input type="file" accept="video/*" ref={videoInputRef} onChange={(e) => setVideo(e.target.files[0])} style={styles.hiddenInput} />
        {video && <video src={URL.createObjectURL(video)} controls style={styles.previewVideo} />}

        <button type="button" onClick={() => thumbnailInputRef.current.click()} style={styles.customButton}> Select Thumbnail</button>
        <input type="file" accept="image/*" ref={thumbnailInputRef} onChange={(e) => setThumbnail(e.target.files[0])} style={styles.hiddenInput}/>
        {thumbnail && <img src={URL.createObjectURL(thumbnail)} alt="Thumbnail Preview" style={styles.previewImage} />}

        <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required style={styles.input} placeholder='Write title'/>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} style={styles.textarea} placeholder='Description'/>

        <div style={styles.linkGroup}>
          {links.map((link, index) => (
            <div key={index} style={styles.groupItem}>
              <input type="text" value={link} onChange={(e) => handleLinkChange(index, e.target.value)} style={styles.input} />
            </div>
          ))}
          <button type="button" onClick={addLink} style={styles.button}> Add Link </button>
        </div>

        <div style={styles.productImageGroup}>
          <button type="button" onClick={() => productImageInputRef.current.click()} style={styles.customButton} > Select Product Images </button>
          <input type="file" accept="image/*" multiple ref={productImageInputRef} onChange={handleProductImageChange} style={styles.hiddenInput} />
          {productImages.length > 0 && (
            <div style={styles.previewGroup}>
              {Array.from(productImages).map((file, index) => (
                <img key={index} src={URL.createObjectURL(file)} alt={`Product ${index}`} style={styles.previewImage}/>
              ))}
            </div>
          )}
        </div>

        <div style={styles.socialMediaGroup}>
          <button type="button" onClick={() => socialMediaInputRef.current.click()} style={styles.customButton} > Select Social Media Icons </button>
          <input type="file" accept="image/*" multiple ref={socialMediaInputRef} onChange={handleSocialMediaChange} style={styles.hiddenInput}/>
          {socialMediaIcons.length > 0 && (
            <div style={styles.iconPreview}>
              {Array.from(socialMediaIcons).map((file, index) => (
                <div key={index} style={styles.iconItem}>
                  <img src={URL.createObjectURL(file)} alt={`Icon ${index}`} style={styles.iconImage} />
                </div>
              ))}
            </div>
          )}
        </div>
        <button type="submit" style={styles.button} id='Up'> Upload </button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    // margin: '30px',
    maxHeight: '88vh', 
    overflowY: 'auto', 
    // backgroundColor:'b'
  },
  form: {
    display: 'grid',
    flexDirection: 'column',
    maxWidth: '400px',
    margin: 'auto',
    // height:'auto'
  },
  label: {
    marginBottom: '5px',
  },
  input: {
    marginBottom: '10px',
    padding: '20px',
    fontSize: '16px',
    backgroundColor: 'rgb(46,46,46)',
    border: 'none',
    borderRadius: '5px',

  },
  textarea: {
    marginBottom: '10px',
    padding: '20px',
    fontSize: '16px',
    resize: 'vertical',
    backgroundColor: 'rgb(46,46,46)',
    border: 'none',
    borderRadius: '5px',
  },
  linkGroup: {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '10px',
  },
  productImageGroup: {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '10px',
  },
  socialMediaGroup: {
    display: 'flex',
    flexDirection: 'column',
    marginBottom: '10px',
  },
  groupItem: {
    display: 'flex',
    justifyContent: 'space-between',
  },
  button: {
    padding: '18px',
    fontSize: '20px',
    cursor: 'pointer',
    backgroundColor: 'rgb(46,46,46)',
    color: 'white',
    borderRadius: '5px',
    border: 'none',
    marginTop: '10px',
  },

  customButton: {
    padding: '20px',
    fontSize: '20px',
    cursor: 'pointer',
    backgroundColor: 'rgb(46,46,46)',
    color: 'white',
    borderRadius: '5px',
    border: 'none',
    marginBottom: '10px',
  },
  hiddenInput: {
    display: 'none',
  },
  previewGroup: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  previewVideo: {
    width: '310px',
    height: '100px',
    objectFit: 'cover',
    marginTop: '2px',
    marginBottom: '10px',
    borderRadius: '5px',
  },
  previewImage: {
    width: '300px',
    height: '100px',
    objectFit: 'cover',
    marginRight: '10px',
    marginBottom: '10px',
    borderRadius: '5px',
  },
  iconPreview: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  iconItem: {
    marginRight: '10px',
    marginBottom: '10px',
  },
  iconImage: {
    width: '50px',
    height: '50px',
    objectFit: 'cover',
  },
};

export default CreateContent;
