import React, { useState, useEffect } from "react";
import slider2 from '../src/images/sl1.webp';
import slider1 from '../src/images/sl2.webp';
import slider3 from '../src/images/sl3.webp';
import slider4 from '../src/images/sl4.webp';
import CustomSlider from "./components/custom.slider";
import "./style.css";

const HomeSlider = () => {
  const [activeSlideIndex, setActiveSlideIndex] = useState(0);


  const [zoomedIndex, setZoomedIndex] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setZoomedIndex(0); 
      setIsLoading(false);
    }, 500); 

    return () => clearTimeout(timer);
  }, []);

  const handleAfterChange = (current) => {
    setZoomedIndex(current); 
  };

  const s = {
    dots: true,
    infinite: true, 
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000, 
    afterChange: handleAfterChange,
    centerMode: true, 
    centerPadding: '0', 
    arrows: false, 
  };


  const images = [
    { imgURL: slider4, imgAlt: "Image 1" },
    { imgURL: slider2, imgAlt: "Image 2" },
    { imgURL: slider3, imgAlt: "img-3" },
    { imgURL: slider1, imgAlt: "img-4" }
  ];

  // useEffect(() => {
  //   const interval = setInterval(() => {
  //     setActiveSlideIndex((prevIndex) => (prevIndex + 1) % images.length);
  //   }, 5000);

  //   return () => clearInterval(interval);
  // }, [images.length]);

  return (
    <div className="home-content">
      <div className="slider-container">
        <CustomSlider {...s}>
          {images.map((image, index) => (
            <div key={index} className="slide-wrapper">
              <img
                src={image.imgURL}
                alt={image.imgAlt}
                className={`slide-image ${index === zoomedIndex ? 'slide-zoomed' : ''}`}
              />
            </div>
          ))}
        </CustomSlider>
      </div>
    </div>
  );
};

export default HomeSlider;
