import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import './Completeview.css';
import './ProductDetail.css';
import ProductGrid from './ProductGrid';
import ShoppingCart from './ShoppingCart'; // Import the ShoppingCart component

const CompleteView = () => {
  const location = useLocation();
  const { product } = location.state;
  const [initialLoad, setInitialLoad] = useState(true);
  const [cartItems, setCartItems] = useState([]);
  const [quantity, setQuantity] = useState(1); // Default quantity
  const popupRef = useRef(null);

  useEffect(() => {
    // Load cart items from localStorage
    const savedCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
    setCartItems(savedCartItems);

    window.scrollTo(0, 0);
    const timer = setTimeout(() => {
      setInitialLoad(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Save cart items to localStorage whenever cartItems changes
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  }, [cartItems]);

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value, 10));
  };

  const handleAddToCart = () => {
    setCartItems((prevItems) => [
      ...prevItems,
      {
        id: product.id,
        name: product.title,
        price: product.price,
        image: product.frontImage, // Ensure this path is correct
        color: product.colors[0] || 'Default Color',
        size: product.sizes[0] || 'Default Size',
        quantity: quantity,
      },
    ]);
  };

  const removeFromCart = (id) => {
    setCartItems((prevItems) => prevItems.filter(item => item.id !== id));
  };

  const cartTotal = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <>
      <div className="complete-view">
        <div className="frames">
          {[product.frontImage, product.backImage, product.extraImage1, product.extraImage2].map((src, index) => (
            <div className="frame" key={index}>
              <img
                src={`http://localhost:8000/${src}`}
                alt={product.title}
                className={initialLoad ? 'initial-zoom' : ''}
              />
            </div>
          ))}
        </div>

        <div className="product-info">
          <h1>{product.title}</h1>
          <div className="price">
            <span className="current-price">Rs.{product.price}</span>
            <span className="discount">Save {product.discount}% right now</span>
          </div>
          <div className="features">
            <h4>Features</h4>
            <ul>
              <li>{product.description}</li>
            </ul>
          </div>

          <div className="colors">
            <h4>Colors</h4>
            <div className="color-options">
              {product.colors && product.colors.length > 0 ? (
                product.colors.map((color, index) => (
                  <button key={index} className={`color ${color}`} />
                ))
              ) : (
                <p>No colors available</p>
              )}
            </div>
          </div>

          <div className="Size-option">
            <h4>Size</h4>
            <div className="Size-options">
              {product.sizes.map((size, index) => (
                <button key={index} className={`size ${size}`}>{size}</button>
              ))}
            </div>

            <div className="selectitem">
              <select name="quantity" id="quantity" onChange={handleQuantityChange} value={quantity}>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
              </select>
            </div>
          </div>

          <div className="action-buttons">
            <button className="add-to-cart" onClick={handleAddToCart}>Add to Cart</button>
            <button className="wishlist">
              <span> ♡ </span>
            </button>
          </div>
        </div>
      </div>

      <br />
      

      <ProductGrid />
    </>
  );
};

export default CompleteView;
