import React, { useEffect, useState } from 'react';
import im1 from './images/m2.jpg';

const MensSlider = () => {
  const [zoomIn, setZoomIn] = useState(false);
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setZoomIn(true);
    setFadeIn(true);
  }, []);

  return (
    <>
      <div className="mensslider" style={{ width: '100vw' }}>
        <div 
          className="frameofslider" 
          style={{ width: '100%', height: '90vh', overflow: 'hidden', position: 'relative' }}
        >
          <img 
            src={im1} 
            alt="Men's Fashion" 
            style={{ 
              height: '100%', 
              width: '100%', 
              objectFit: 'cover', 
              display: 'block', 
              transform: zoomIn ? 'scale(1)' : 'scale(1.2)', 
              transition: 'transform 1s ease-in-out, opacity 9s ease-in-out', 
            }} 
          />
        </div>
      </div>
    </>
  );
};

export default MensSlider;
