import React, { useState, useEffect } from 'react';
import './TextSlider.css'; 

const TextSlider = () => {
  const texts = ["Our collection features a range of stylish and versatile outfits perfect for any casual occasion.", 
    "Active Slide: When a slide becomes active, it's moved into the visible area with a smooth", 
    "This setup makes the text slide in from right to left with smooth animation. You can use the", 
    "slider where each of the four slides comes from the right to the center one by one"
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % texts.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [texts.length]);

  return (
    <div className="text-slider">
      <div className="slide-container">
        {texts.map((text, index) => (
          <div
            key={index}
            className={`slide ${index === currentIndex ? 'active' : ''}`}
            style={{}}
          >
            {text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default TextSlider;
