import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import "./CreatePage.css"

import { FiEdit2 } from "react-icons/fi";
import profile from './images/profile.jpg'



import { Box, IconButton, Avatar, Typography, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import { Dashboard, VideoLibrary, Analytics, Comment, Subtitles, Copyright, Settings, Feedback, People,ThumbDown,ThumbUp} from '@mui/icons-material';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import HeartBrokenIcon from '@mui/icons-material/HeartBroken';
import VideoCallSharpIcon from '@mui/icons-material/VideoCallSharp';


const CreatePage = () => {


    const [selectedVideo, setSelectedVideo] = useState(null);
    const [selectedImage, setSelectedImage] = useState(null);
    const [videoPreview, setVideoPreview] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
  
    const [videoList, setVideoList] = useState([]);
    const [imageList, setImageList] = useState([]);
  
    const handleVideoChange = (e) => {
      setSelectedVideo(e.target.files[0]);
      setVideoPreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleImageChange = (e) => {
      setSelectedImage(e.target.files[0]);
      setImagePreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleUpload = () => {
      if (selectedVideo && selectedImage) {
        const videoFormData = new FormData();
        videoFormData.append('video', selectedVideo);
  
        axios.post('http://localhost:8000/upload-video', videoFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Video upload successful");
              fetchVideos();
            } else {
              console.log("Video upload failed");
            }
          })
          .catch(err => console.error('Error uploading video:', err));
  
        const imageFormData = new FormData();
        imageFormData.append('image', selectedImage);
  
        axios.post('http://localhost:8000/upload-image', imageFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Image upload successful");
              fetchImages();
            } else {
              console.log("Image upload failed");
            }
          })
          .catch(err => console.error('Error uploading image:', err));
      } else {
        console.log("Please select both video and image files.");
      }
    };
  
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:8000/videos');
        setVideoList(response.data);
      } catch (error) {
        console.error('Error fetching videos:', error);
      }
    };
  
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:8000/images');
        setImageList(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };
  
    useEffect(() => {
      fetchVideos();
      fetchImages();
    }, []);
  
    const videoInputRef = useRef(null);
    const imageInputRef = useRef(null);




    const [selectedItem, setSelectedItem] = useState('Dashboard');

    const handleItemClick = (item) => {
      setSelectedItem(item);
    };
  
    const renderContent = () => {
      switch (selectedItem) {
        case 'Create' :
          return <Typography variant='h6'> Create Videos </Typography>
        case 'Dashboard':
          return <Typography variant="h6">Dashboard Content</Typography>;
        case 'Content':
          return <Typography variant="h6">Content Library</Typography>;
        case 'Analytics':
          return <Typography variant="h6">Analytics Data</Typography>;
        case 'Comments':
          return <Typography variant="h6">User Comments</Typography>;
        case 'Subtitles':
          return <Typography variant="h6">Subtitle Management</Typography>;
        case 'Copyright':
          return <Typography variant="h6">Copyright Information</Typography>;
        case 'Likes':
          return <Typography variant="h6">Total Likes: 1500</Typography>;
        case 'Dislikes':
          return <Typography variant="h6">Total Dislikes: 300</Typography>;
        case 'Subscribers':
          return <Typography variant="h6">Total Subscribers: 1200</Typography>;
        case 'Settings':
          return <Typography variant="h6">Settings Page</Typography>;
        case 'Feedback':
          return <Typography variant="h6">Feedback Page</Typography>;
        default:
          return <Typography variant="h6">Dashboard Content</Typography>;
      }
    };


  return (
    <>

      <div className='create'>
      
      <Box sx={{ display: 'flex', }}>
      <Box sx={{height: '120vh',width: '240px',backgroundColor: '#181818',color: '#fff',padding: '10px 20px',display: 'flex',flexDirection: 'column',alignItems: 'center',boxShadow: '2px 0 5px rgba(0,0,0,0.1)',}}>
        <Box sx={{ marginBottom: '20px', textAlign: 'center' }}>
          <Avatar
            src={profile}
            alt="Gauri Shankar Khadga"
            sx={{ width: 80, height: 80 }}
          />
          <Typography variant="body1" sx={{ marginTop: '10px' }}>
            Your channel
          </Typography>
          <Typography variant="body2" sx={{ color: 'gray' }}>
            Gauri Shankar Khadga
          </Typography>
        </Box>

        <List component="nav" style={{overflow:'auto',height:'1000px',scrollbarWidth:'none',marginTop:'-30px'}}>
          <ListItem button onClick={() => handleItemClick('Create')}>
            <ListItemIcon>
              <VideoCallSharpIcon sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Create" />
          </ListItem>

          <ListItem button onClick={() => handleItemClick('Dashboard')}>
            <ListItemIcon>
              <Dashboard sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Dashboard" />
          </ListItem>

          <ListItem button onClick={() => handleItemClick('Content')}>
            <ListItemIcon>
              <VideoLibrary sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Content" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Analytics')}>
            <ListItemIcon>
              <Analytics sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Analytics" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Copyright')}>
            <ListItemIcon>
              <Copyright sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Copyright" />
          </ListItem>
          
          <ListItem button onClick={() => handleItemClick('Likes')}>
            <ListItemIcon>
              <ThumbUp sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Likes" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Dislikes')}>
            <ListItemIcon>
              <ThumbDown sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Dislikes" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Subscribers')}>
            <ListItemIcon>
              <People sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Subscribers" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Settings')}>
            <ListItemIcon>
              <Settings sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Settings" />
          </ListItem>
          <ListItem button onClick={() => handleItemClick('Feedback')}>
            <ListItemIcon>
              <Feedback sx={{ color: '#fff' }} />
            </ListItemIcon>
            <ListItemText primary="Send feedback" />
          </ListItem>
        </List>
      </Box>
      <hr />

      <Box sx={{ flex: 1, padding: '20px', backgroundColor: 'black',width:'1300px',position:'static' }}>
      <div className="divider" style={{borderTop:'2px solid white',width:'1320px',marginLeft:'-40px'}}></div>
        {renderContent()}
      </Box>
    </Box>

      </div>

    </>
  )
}
