import React, { useState } from "react";
import SignInForm from "./SignIn";
import SignUpForm from "./SignUp";
import "./LoginSignup.css";



const LoginSignup = () => {
  const [type, setType] = useState("signIn");
  const handleOnClick = text => {
    if (text !== type) {
      setType(text);
      return;
    }
  };





  const containerClass =
    "container " + (type === "signUp" ? "right-panel-active" : "");
  return (
    <div className="ls">
    <div className="loginsignpage">
      <h2>Sign in/up Form</h2>
      <div className={containerClass} id="container">
        <SignUpForm />
        <SignInForm />
        <div className="overlay-container">
          <div className="overlay">
            <div className="overlay-panel overlay-left" style={{textAlign:'center',width:'400px'}}>
              <h1>Welcome Back!</h1>
              <p>
                Connect with Us
              </p>
              <button
                className="ghost"
                id="signIn"
                onClick={() => handleOnClick("signIn")}
              >
                Sign In
              </button>
            </div>
            <div className="overlay-panel overlay-right">
              <h1>Hello, Friend!</h1>
              <p>Enter your personal details and start </p>
              <button className="ghost " id="signUp" onClick={() => handleOnClick("signUp")}>Sign Up</button>
            </div>
          </div>
        </div>
      </div>
    </div>
    </div>
  );
}

export default LoginSignup;
