import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import QRCode from 'qrcode.react';
import './Payment.css';

// Define your OneSignal credentials
const ONE_SIGNAL_APP_ID = '8c3f4ec5-0843-49be-b655-e2cee1b396fb';
const ONE_SIGNAL_REST_API_ID = 'ZGRiZWUzZTQtNDNkOS00Njk4LTk5ZDktYzI5ZGQzNTljYTc3';

const Payment = () => {
  const location = useLocation();
  const { product, quantity } = location.state || {};
  const [paymentMethod, setPaymentMethod] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [cvv, setCvv] = useState('');
  const [address, setAddress] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [mobileNumberError, setMobileNumberError] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [userLocation, setUserLocation] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);

  const handlePaymentMethodChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const formatCardNumber = (value) => {
    const numericValue = value.replace(/\D/g, '');
    const formattedValue = numericValue.replace(/(.{4})/g, '$1 ').trim().slice(0, 19);
    setCardNumber(formattedValue);
  };

  const formatExpiryDate = (value) => {
    const numericValue = value.replace(/\D/g, '');
    const formattedValue = numericValue.replace(/(\d{2})(\d{0,2})/, '$1/$2').slice(0, 5);
    setExpiryDate(formattedValue);
  };

  const formatCvv = (value) => {
    const numericValue = value.replace(/\D/g, '').slice(0, 3);
    setCvv(numericValue);
  };

  const validateCardNumber = (value) => {
    const numericValue = value.replace(/\D/g, '');
    const isValid = (num) => {
      let sum = 0;
      let alternate = false;
      for (let i = num.length - 1; i >= 0; i--) {
        let n = parseInt(num.charAt(i), 10);
        if (alternate) {
          n *= 2;
          if (n > 9) n -= 9;
        }
        sum += n;
        alternate = !alternate;
      }
      return sum % 10 === 0;
    };
    return numericValue.length === 16 && isValid(numericValue);
  };

  const validateMobileNumber = (value) => {
    const numericValue = value.replace(/\D/g, '');
    if (numericValue.length === 10 && /^[789]\d{9}$/.test(numericValue)) {
      setMobileNumberError('');
      return true;
    } else {
      setMobileNumberError('Invalid mobile number. Must be 10 digits long and start with 7, 8, or 9.');
      return false;
    }
  };

  const fetchHumanReadableAddress = async (latitude, longitude) => {
    try {
      const response = await fetch(`https://nominatim.openstreetmap.org/reverse?lat=${latitude}&lon=${longitude}&format=json`);
      if (!response.ok) throw new Error('Failed to fetch address');
      const data = await response.json();
      return data.display_name;
    } catch (error) {
      console.error('Error fetching address:', error);
      return 'Unable to fetch address.';
    }
  };

  const fetchCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          const address = await fetchHumanReadableAddress(latitude, longitude);
          setAddress(address);
          setUserLocation(`Lat: ${latitude}, Long: ${longitude}`);
        },
        (error) => {
          console.error('Error fetching location:', error);
          alert('Unable to fetch location. Please enter your address manually.');
        }
      );
    } else {
      alert('Geolocation is not supported by this browser.');
    }
  };

  // Function to send SMS
  const sendSMS = async (mobileNumber, message) => {
    try {
      const response = await fetch('http://localhost:8000/api/send-sms', { // Replace with your SMS sending endpoint
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Basic ${btoa(`${ONE_SIGNAL_REST_API_ID}:`)}` // Add Basic Auth header if required
        },
        body: JSON.stringify({ mobileNumber, message }),
      });

      if (!response.ok) {
        throw new Error('Failed to send SMS');
      }
    } catch (error) {
      console.error('Error sending SMS:', error);
    }
  };

  const handlePaymentSubmission = async () => {
    if (!address || !mobileNumber) {
      alert('Please enter both address and mobile number.');
      return;
    }

    if (!validateMobileNumber(mobileNumber)) {
      alert('Please enter a valid mobile number.');
      return;
    }

    if (!otpVerified) {
      alert('Please verify your OTP before proceeding with payment.');
      return;
    }

    const totalPrice = product.price * quantity;
    const paymentData = {
      product,
      quantity,
      paymentMethod,
      price: totalPrice,
      address,
      mobileNumber,
      cardNumber: paymentMethod === 'Card Payment' ? cardNumber : undefined,
      expiryDate: paymentMethod === 'Card Payment' ? expiryDate : undefined,
      cvv: paymentMethod === 'Card Payment' ? cvv : undefined,
    };

    try {
      const response = await fetch('http://localhost:8000/api/payments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(paymentData),
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Payment stored:', data);

        // Send SMS notification
        const smsMessage = `Payment successful for ${product.title}. Amount: Rs.${totalPrice}.`;
        await sendSMS(mobileNumber, smsMessage);

        setShowNotification(true);
        setTimeout(() => setShowNotification(false), 4000);
      } else {
        throw new Error('Failed to store payment data');
      }
    } catch (error) {
      console.error('Error submitting payment:', error);
    }
  };

  const handleSendOtp = async () => {
    if (!validateMobileNumber(mobileNumber)) return;

    const otpMessage = 'Your OTP code for payment verification is: 123456'; // Generate a random OTP here
    await sendSMS(mobileNumber, otpMessage);
    setOtpSent(true);
    alert('OTP sent to your mobile number.');
  };

  const handleVerifyOtp = () => {
    if (otp === '123456') { // Replace with actual OTP verification logic
      setOtpVerified(true);
      alert('OTP verified successfully!');
    } else {
      alert('Invalid OTP. Please try again.');
    }
  };

  if (!product) {
    return <p className="no-product">No product selected.</p>;
  }

  return (
    <div className="paymentcontainer">
      <div className="payment-view">
        <h1 className="page-title">Payment</h1>
        <table className="payment-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Product Title</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="product-image-cell">
                <img
                  src={`http://localhost:8000/${product.frontImage}`}
                  alt={product.title}
                  className="product-image"
                />
              </td>
              <td>{product.title}</td>
              <td>Rs.{product.price}</td>
              <td>{quantity}</td>
              <td>Rs.{product.price * quantity}</td>
            </tr>
          </tbody>
        </table>

        <h3 className="payment-method-title">Select Payment Method</h3>
        <div className="payment-methods">
          <div className={`payment-option ${paymentMethod === 'COD' ? 'selected' : ''}`}>
            <input
              type="radio"
              id="cod"
              name="payment-method"
              value="COD"
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="cod">Cash on Delivery</label>
          </div>
          <div className={`payment-option ${paymentMethod === 'Card Payment' ? 'selected' : ''}`}>
            <input
              type="radio"
              id="card"
              name="payment-method"
              value="Card Payment"
              onChange={handlePaymentMethodChange}
            />
            <label htmlFor="card">Card Payment</label>
          </div>
        </div>

        {paymentMethod === 'Card Payment' && (
          <div className="card-details">
            <label>
              Card Number:
              <input
                type="text"
                value={cardNumber}
                onChange={(e) => formatCardNumber(e.target.value)}
                maxLength={19}
                required
              />
            </label>
            <label>
              Expiry Date:
              <input
                type="text"
                value={expiryDate}
                onChange={(e) => formatExpiryDate(e.target.value)}
                maxLength={5}
                placeholder="MM/YY"
                required
              />
            </label>
            <label>
              CVV:
              <input
                type="text"
                value={cvv}
                onChange={(e) => formatCvv(e.target.value)}
                maxLength={3}
                required
              />
            </label>
          </div>
        )}

        <label>
          Address:
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </label>

        <label>
          Mobile Number:
          <input
            type="text"
            value={mobileNumber}
            onChange={(e) => {
              setMobileNumber(e.target.value);
              validateMobileNumber(e.target.value);
            }}
            maxLength={10}
            required
          />
          {mobileNumberError && <span className="error">{mobileNumberError}</span>}
        </label>

        <div className="otp-section">
          <button onClick={handleSendOtp} disabled={otpSent}>Send OTP</button>
          {otpSent && (
            <div>
              <label>
                Enter OTP:
                <input
                  type="text"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                />
              </label>
              <button onClick={handleVerifyOtp}>Verify OTP</button>
            </div>
          )}
        </div>

        <button className="pay-button" onClick={handlePaymentSubmission}>Pay Now</button>

        {showNotification && (
          <div className="notification">
            <p>Payment was successful!</p>
          </div>
        )}
      </div>

      <QRCode value={`Product: ${product.title}, Price: Rs.${product.price * quantity}`} />
    </div>
  );
};

export default Payment;
