    const audio = audioRef.current;
    const newProgress = event.target.value;
    audio.currentTime = (newProgress / 100) * audio.duration;
    setProgress(newProgress);
  };

  const selectTrack = (index) => {
    setCurrentTrackIndex(index);
    setIsPlaying(true);
  };

  useEffect(() => {
    const audio = audioRef.current;
    audio.pause();
    audio.src = tracks[currentTrackIndex].src;
    audio.load();
    if (isPlaying) {
      audio.play();
    }
  }, [currentTrackIndex, isPlaying]);


  const story = [
    { id:1, source:badshah },
    { id:2, source:hony },
    { id:3, source:neha },
  ];






  return (
    <>
    <div className="musicplayercontainer" style={{marginTop:'-60px',marginLeft:'-10px'}}>
    <div className="centerstory">
      <div className="story">
        {
          story.map((item, index) => (
            <img src={item.source} key={index} alt="Server Error" aria-placeholder='Artist' className='images' style={{backgroundColor:''}}/>
          ))
        }
      </div>
    </div>

      <section>
        <div className="musicp">
          <div className="music-player" >

            <audio ref={audioRef} autoPlay={false} />
            
            <img src={tracks[currentTrackIndex].thumbnail} alt="" className='honysing' />
            {/* <img src={tracks[currentTrackIndex].thumbnail} alt="" className='honysingblur' /> */}

            <div className="control">
              <FaRandom className="icon" onClick={shuffleTrack} />
              <FaBackward className="icon" onClick={prevTrack} />
              <div className="play-pause" onClick={togglePlayPause}>
                {isPlaying ? <FaPause className="icon" /> : <FaPlay className="icon" />}
              </div>
              <FaForward className="icon" onClick={nextTrack} />
              <FaRedo className="icon" />
            </div>

            <div className="progress-bar">
              <input
                type="range"
                min="0"
                max="100"
                value={progress}
                onChange={handleProgressChange}
                className='rotate'
              />
            </div>
          </div>
        </div>

        <div className="playlist" style={{
          marginTop: '90px',
          maxHeight: '550px',
          scrollbarWidth: 'none',
          backgroundColor: 'rgb(29,42,53)',
          marginLeft:'50px',
          width:'1050px',
          boxShadow: '0 4px 80px rgba(17, 109, 237, 0.3)',
          backgroundImage: 'url("imagess.jpeg")',
        }}>


          {tracks.map((track, index) => (
            <div id='list'
              key={index}
              className={`track ${currentTrackIndex === index ? 'active' : ''}`}
              onClick={() => selectTrack(index)}
            >
              <img src={track.thumbnail} alt={track.title} className="thumbnail" />
              <div className="track-info">
                <div className="track-title">{track.title}</div>
                <div className="track-artist">{track.artist}</div>
              </div>
            </div>
          ))}
        </div>
      </section>
      </div>
    </>
  );
}

export default MusicPlayer;
