import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import "./CreatePage.css"
import Home from './Home'

import { FiEdit2 } from "react-icons/fi";
import profile from './images/profile.jpg'

const CreateContent = () => {


    
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [selectedImage, setSelectedImage] = useState(null);
    const [videoPreview, setVideoPreview] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
  
    const [videoList, setVideoList] = useState([]);
    const [imageList, setImageList] = useState([]);
  
    const handleVideoChange = (e) => {
      setSelectedVideo(e.target.files[0]);
      setVideoPreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleImageChange = (e) => {
      setSelectedImage(e.target.files[0]);
      setImagePreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleUpload = () => {
      if (selectedVideo && selectedImage) {
        const videoFormData = new FormData();
        videoFormData.append('video', selectedVideo);
  
        axios.post('http://localhost:8000/upload-video', videoFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Video upload successful");
              fetchVideos();
            } else {
              console.log("Video upload failed");
            }
          })
          .catch(err => console.error('Error uploading video:', err));
  
        const imageFormData = new FormData();
        imageFormData.append('image', selectedImage);
  
        axios.post('http://localhost:8000/upload-image', imageFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Image upload successful");
              fetchImages();
            } else {
              console.log("Image upload failed");
            }
          })
          .catch(err => console.error('Error uploading image:', err));
      } else {
        console.log("Please select both video and image files.");
      }
    };
  
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:8000/videos');
        setVideoList(response.data);
      } catch (error) {
        console.error('Error fetching videos:', error);
      }
    };
  
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:8000/images');
        setImageList(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };
  
    useEffect(() => {
      fetchVideos();
      fetchImages();
    }, []);
  
    const videoInputRef = useRef(null);
    const imageInputRef = useRef(null);










  return (
    <>

    <div className="create">
        <div className="bigbox">
          <div className="uploadbox">
              <ul>
                <input id='selv' type="file" ref={videoInputRef} style={{ display: 'none' }} onChange={handleVideoChange} accept="video/*" /> 
                <button id='selectvideos' onClick={() => videoInputRef.current.click()}> Click Select Video 
                  {videoPreview && <video id='videoView' src={videoPreview} controls />}
                </button> 
            </ul>

            <ul>
            <input type="file" ref={imageInputRef} style={{ display: 'none' }} onChange={handleImageChange} accept="image/*" />
              <button id='selectthumbnail' onClick={() => imageInputRef.current.click()}> Click Select Thumbnail 
                {imagePreview && <img id='imageView' src={imagePreview} alt="Image Preview" />}
              </button> 
            </ul>
          </div>



          <div className="titlediscription">
            <ul>
              <input id='title' type="text" placeholder='Write title of your video' required/>
            </ul>
            <ul>
              <input id='discribtion' type="text" placeholder='Discription'/>
            </ul>
          </div>

          <div className="impselect" style={{textAlign:'center'}}>

            <p id='para' style={{display:'flex'}}> <p style={{fontSize:'18px',marginTop:'5px'}}> 
              Video types: 
              <select name="selectoption" id="selectionofid">
                <option value="public">Public</option>
                <option value="private">Private</option>
                <option value="kids">Kids</option>
              </select>

              </p> 
              
              </p>
            <p id='para'><button id='upload' onClick={handleUpload}> Upload </button></p>
          </div>

        </div>
    </div>
    </>
  )
}

export default CreateContent
