import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import "./CreatePage.css"

import { FiEdit2 } from "react-icons/fi";
import profile from './images/profile.jpg'

import { Box, IconButton, Avatar, Typography, List, ListItem, ListItemIcon, ListItemText,Button,Input } from '@mui/material';
import { Dashboard, VideoLibrary, Analytics, Comment, Subtitles, Copyright, Settings, Feedback } from '@mui/icons-material';


const CreatePage = () => {


    const [selectedVideo, setSelectedVideo] = useState(null);
    const [selectedImage, setSelectedImage] = useState(null);
    const [videoPreview, setVideoPreview] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
  
    const [videoList, setVideoList] = useState([]);
    const [imageList, setImageList] = useState([]);
  
    const handleVideoChange = (e) => {
      setSelectedVideo(e.target.files[0]);
      setVideoPreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleImageChange = (e) => {
      setSelectedImage(e.target.files[0]);
      setImagePreview(URL.createObjectURL(e.target.files[0]));
    };
  
    const handleUpload = () => {
      if (selectedVideo && selectedImage) {
        const videoFormData = new FormData();
        videoFormData.append('video', selectedVideo);
  
        axios.post('http://localhost:8000/upload-video', videoFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Video upload successful");
              fetchVideos();
            } else {
              console.log("Video upload failed");
            }
          })
          .catch(err => console.error('Error uploading video:', err));
  
        const imageFormData = new FormData();
        imageFormData.append('image', selectedImage);
  
        axios.post('http://localhost:8000/upload-image', imageFormData)
          .then(res => {
            if (res.data.status === "Success") {
              console.log("Image upload successful");
              fetchImages();
            } else {
              console.log("Image upload failed");
            }
          })
          .catch(err => console.error('Error uploading image:', err));
      } else {
        console.log("Please select both video and image files.");
      }
    };
  
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:8000/videos');
        setVideoList(response.data);
      } catch (error) {
        console.error('Error fetching videos:', error);
      }
    };
  
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://localhost:8000/images');
        setImageList(response.data);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    };
  
    useEffect(() => {
      fetchVideos();
      fetchImages();
    }, []);
  
    const videoInputRef = useRef(null);
    const imageInputRef = useRef(null);








  return (
    <>


    <Box
      sx={{
        height: '100vh',
        width: '240px',
        backgroundColor: '#181818',
        backgroundColor:'red',
        color: '#fff',
        padding: '10px 20px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        boxShadow: '2px 0 5px rgba(0,0,0,0.1)',
      }}
    >
      <Box sx={{ marginBottom: '20px' }}>
        <Avatar
          src="/path/to/avatar.jpg"
          alt="Gauri Shankar Khadga"
          sx={{ width: 80, height: 80 }}
        />
        <Typography variant="body1" sx={{ marginTop: '10px' }}>
          Your channel
        </Typography>
        <Typography variant="body2" sx={{ color: 'gray' }}>
          Gauri Shankar Khadga
        </Typography>
      </Box>
      
      <List component="nav">
        <ListItem button>
          <ListItemIcon>
            <Dashboard sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Dashboard" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <VideoLibrary sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Content" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Analytics sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Analytics" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Comment sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Comments" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Subtitles sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Subtitles" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Copyright sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Copyright" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Settings sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Settings" />
        </ListItem>
        <ListItem button>
          <ListItemIcon>
            <Feedback sx={{ color: '#fff' }} />
          </ListItemIcon>
          <ListItemText primary="Send feedback" />
        </ListItem>
      </List>
    </Box>







    <Box className="create" sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: 2,backgroundColor:'green',zIndex:'' }}>
      <Box className="avatar" sx={{ position: 'relative', marginBottom: 2 }}>
        <img id='profileImg' src={profile} alt="not found" style={{ borderRadius: '50%', width: 50, height: 100 }} />
        <IconButton id='editBtn' sx={{ position: 'absolute', top: 10, right: 10 }}>
          <FiEdit2 id='edit' />
        </IconButton>
        <Box className="yourdetail" sx={{ textAlign: 'center', marginTop: 2 }}>
          <Button id='channelname' variant="contained" color="primary">Best Coding</Button>
          <Box id='mid' sx={{ display: 'flex', justifyContent: 'center', marginTop: 2 }}>
            <Button id='view' sx={{ margin: 1 }}>Views<br /><Typography variant="h6">1500</Typography></Button>
            <Button id='subscriber' sx={{ margin: 1 }}>Subscribers<br /><Typography variant="h6">1200</Typography></Button>
            <Button id='like' sx={{ margin: 1 }}>Likes<br /><Typography variant="h6">1000</Typography></Button>
          </Box>
        </Box>
      </Box>
      <Box className="bigbox" sx={{ width: '100%', maxWidth: 600 }}>
        <Box className="uploadbox" sx={{ display: 'flex', justifyContent: 'space-between', marginBottom: 2 }}>
          <Box>
            <Input id='selv' type="file" ref={videoInputRef} style={{ display: 'none' }} onChange={handleVideoChange} accept="video/*" />
            <Button id='selectvideos' variant="outlined" onClick={() => videoInputRef.current.click()}>
              Click Select Video
              {videoPreview && <video id='videoView' src={videoPreview} controls style={{ display: 'block', marginTop: 10 }} />}
            </Button>
          </Box>
          <Box>
            <Input id='seli' type="file" ref={imageInputRef} style={{ display: 'none' }} onChange={handleImageChange} accept="image/*" />
            <Button id='selectthumbnail' variant="outlined" onClick={() => imageInputRef.current.click()}>
              Click Select Thumbnail
              {imagePreview && <img id='imageView' src={imagePreview} alt="Image Preview" style={{ display: 'block', marginTop: 10, width: '100%', height: 'auto' }} />}
            </Button>
          </Box>
        </Box>
        <Box className="titlediscription" sx={{ marginBottom: 2 }}>
          <Input id='title' type="text" placeholder='Write title of your video' fullWidth required sx={{ marginBottom: 1 }} />
          <Input id='discribtion' type="text" placeholder='Description' fullWidth sx={{ marginBottom: 1 }} />
        </Box>
        <Box className="impselect" sx={{ marginTop: 2 }}>
          <Typography id='para'>
            Your Video: <Button id='public' variant="outlined">Public</Button>
          </Typography>
          <Typography id='para'>
            Your Video: <Button id='private' variant="outlined">Private</Button>
          </Typography>
          <Typography id='para'>
            This is for <Button id='kids' variant="outlined">Kids</Button>
          </Typography>
          <Button id='upload' variant="contained" color="primary" onClick={handleUpload} sx={{ marginTop: 2 }}>Upload</Button>
        </Box>
      </Box>
    </Box>


    </>
  )
}
export default CreatePage
