import React, { useState, useEffect,} from 'react';
import ReactPlayer from 'react-player';
// import { Link } from 'react-router-dom';
import { IconButton, InputBase, AppBar, Toolbar, Typography, Box, Drawer, ListItemIcon, ListItemText,Popover,Avatar, Button} from '@mui/material';

import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';














import spimage from './images/spimage.jpg'



import { AiOutlineLike} from "react-icons/ai";
import { AiOutlineDislike } from "react-icons/ai";
import { PiShareFatThin } from "react-icons/pi";
import { LiaDownloadSolid } from "react-icons/lia";
import { IoArrowForward } from "react-icons/io5";
import { CiCircleMore } from "react-icons/ci";

import kamarke from './images/kamarke.jpg';
import nachre from './images/nachre.jpg';
import nathuniya from './images/nathuniya.jpg';
import halka from './images/halka.jpg';



import userimage1 from './images/image1.jpeg';
import userimage2 from './images/image2.jpeg'
import userimage3 from './images/image3.jpeg';
import userimage4 from './images/image4.jpeg';
import userimage5 from './images/image5.jpeg';
import userimage6 from './images/image6.jpeg';
import userimage7 from './images/image7.jpeg';
import userimage8 from './images/image8.jpeg';
import userimage9 from './images/image9.jpeg';
import userimage10 from './images/image10.jpeg';
import userimage11 from './images/image11.jpeg';
import userimage12 from './images/image12.jpeg';


// import './Cartt.css';
import './Home.css'
import { List, ListItem } from '@mui/material';


const Home = ({data}) => {

  const [borderr, setBorder] = useState(false)

  const clickcontent = () => {
    setBorder(true);
  }



 

  const containerStyle = {
    backgroundColor: '#181818',
    color: '#fff',
    padding: '20px',
    borderRadius: '10px',
    maxWidth: '700px',
    margin: '20px auto',
    fontFamily: 'Arial, sans-serif',
  };

  const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '15px',
  };

  const titleStyle = {
    fontSize: '20px',
    fontWeight: 'bold',
    marginRight: '10px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    maxWidth: 'calc(100% - 100px)', // Adjust based on available space
  };

  const subscribeStyle = {
    display: 'flex',
    alignItems: 'center',
  };

  const channelInfoStyle = {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  };

  const channelLogoStyle = {
    width: '40px',
    height: '40px',
    borderRadius: '50%',
    marginRight: '10px',
  };

  const subscribeButtonStyle = {
    marginLeft: '10px',
    padding: '8px 16px',
    backgroundColor: '#ff0000',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontWeight: 'bold',
  };

  const actionButtonStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: 'auto',
    backgroundColor: '#282828',
    padding: '6px 15px',
    borderRadius: '25px',
    cursor: 'pointer',
    marginRight: '10px',
  };

  const iconStyle = {
    fontSize: '16px',
    marginRight: '5px',
  };

  const actionContainerStyle = {
    marginLeft:'150px',
    display: 'flex',
    alignItems: 'center',
  };

  










  const [selectedVideo, setSelectedVideo] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  const [image1, setImage1] = useState([
    { id: 1, source: kamarke, description: "Aanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  1", category: 'Category 1',userchannel: 'coding', view: 1021340,userimage: userimage1 },
    { id: 2, source: nachre, description: "dfgfanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  2", category: 'Category 2',userchannel: 'kids', view: 23467,userimage: userimage2 },
    { id: 3, source: nathuniya, description: "Manali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  3", category: 'Category 1',userchannel: 'cartoon', view: 3234,userimage: userimage3 },
    { id: 4, source: halka, description: "fghanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  4", category: 'Category 2',userchannel: 'best song', view: 4087,userimage: userimage4 },
    { id: 5, source: kamarke, description: "Manali Trance | Yo Yodfh Honey Singh x Neha Kakkar x Lisa  5", category: 'Category 1',userchannel: 'commercial', view: 98755,userimage: userimage5 },
    { id: 6, source: nachre, description: "Manali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  6", category: 'Category 2',userchannel: 'user 6', view: 67345,userimage: userimage6 },
    { id: 7, source: nathuniya, description: "Manali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  7", category: 'Category 1',userchannel: 'user 7', view: 79879,userimage: userimage7 },
    { id: 8, source: halka, description: "oiManali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  8", category: 'Category 2',userchannel: 'user 8', view: 80987,userimage: userimage8 },
    { id: 9, source: kamarke, description: "xManali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  9", category: 'Category 1',userchannel: 'user 9', view: 345679,userimage: userimage9 },
    { id: 10, source: nachre, description: "vanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  10", category: 'Category 2',userchannel: 'user 10', view: 1435670,userimage: userimage10 },
    { id: 11, source: nathuniya, description: "qanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  11", category: 'Category 1',userchannel: 'user 11', view: 132451,userimage: userimage11 },
    { id: 12, source: halka, description: "wanali Trance | Yo Yo Honey Singh x Neha Kakkar x Lisa  12", category: 'Category 2',userchannel: 'user 12', view: 457612,userimage: userimage12 }
  ]);

  
