import React, { useEffect, useState } from "react";
import "./Signup.css";
import Popup from "./Popup";
import { useNavigate } from "react-router-dom";

const Signup = () => {
  const navigate = useNavigate();

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstname] = useState('')
  const [lastName, setLastname]= useState('');
  const [phoneNumber, setNumber]= useState('');
  const [addressLine1, setaddressline]= useState('');
  const [city, setCity]= useState('');
  const [state, setState]= useState('');
  const [otp, setOtp]= useState(false);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');


  

  const verifyOtp = async (email, otp) => {
    try {
      const response = await fetch('https://rappo.onrender.com/verifyOtp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, otp }),
      });

      const result = await response.json();
      console.log(result); // For debugging

      if (result.message === 'OTP verified successfully!') {
        return true;
      } else {
        setErrorMessage(result.message); // Set error message for invalid OTP
        return false;
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      setErrorMessage('An error occurred during OTP verification.');
      return false;
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://rappo.onrender.com/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Checkout data submitted successfully');
        setIsPopupOpen(true); // Ensure the popup opens here
      } else {
        const errorResponse = await response.text();
        alert(`Failed to submit checkout data: ${errorResponse}`);
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting checkout data');
    }
  };

  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Clear previous error messages

    const isVerified = await verifyOtp(formData.email, otp);

    if (isVerified) {
      alert('OTP verified successfully!');
      navigate('/profile'); // Navigate to profile after verification
    } else {
      alert('Invalid OTP. Please try again.');
    }

    setIsPopupOpen(false);
  };
  return (
    <div className="checkout-container">
      <div className="form-section" style={{ display: 'flex' }}>
        <form className="checkout-form" onSubmit={handleSubmit}>
          <h3 className="shippinginfo">Shipping Information</h3>
          <input
            type="email"
            name="email"
            placeholder="Email address"
            value={formData.email}
            onChange={handleChange}
            required
            className="form-input"
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            value={formData.password}
            onChange={handleChange}
            required
            className="form-input"
          />
          <div className="name-fields">
            <input
              type="text"
              name="firstName"
              placeholder="First name"
              value={formData.firstName}
              onChange={handleChange}
              required
              className="form-input half-width"
              style={{ marginRight: '30px', marginLeft: '10px' }}
            />
            <input
              type="text"
              name="lastName"
              placeholder="Last name"
              value={formData.lastName}
              onChange={handleChange}
              required
              className="form-input half-width"
            />
          </div>
          <input
            type="text"
            name="phoneNumber"
            placeholder="Phone number"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
            className="form-input"
          />
          <input
            type="text"
            name="addressLine1"
            placeholder="Enter Your Address"
            value={formData.addressLine1}
            onChange={handleChange}
            required
            className="form-input"
          />
          <div className="location-fields">
            <input
              type="text"
              name="city"
              placeholder="City"
              value={formData.city}
              onChange={handleChange}
              required
              className="form-input half-width"
              style={{ marginRight: '30px', marginLeft: '10px' }}
            />
            <input
              type="text"
              name="state"
              placeholder="State"
              value={formData.state}
              onChange={handleChange}
              required
              className="form-input half-width"
            />
          </div>

          <button type="submit" className="confirm-payment-btn">Send</button>
          
          <Popup isOpen={isPopupOpen} onClose={() => setIsPopupOpen(false)} title="Enter OTP">
            <form onSubmit={handleOtpSubmit}>
              <input
                type="text"
                name="otp"
                placeholder="Enter your OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                required
                className="form-input"
              />
              <button type="submit" className="confirm-payment-btn">Verify OTP</button>
            </form>
          </Popup>
        </form>
      </div>
    </div>
  );
};

export default Signup;
