import React, { useState, useEffect } from 'react';
import './ImageSliderr.css'; // Import the CSS for animations
import slider1 from './maincomponent/images/m1.webp';
import slider2 from './maincomponent/images/m2.jpg';
import slider3 from './maincomponent/images/m3.jpg';
import slider4 from './maincomponent/images/m4.webp';

const ImageSliderr = () => {
  const images = [slider1, slider2, slider3, slider4];
  
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000); // Change image every 5 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="image-slider-wrapper">
      <div className="slide-container-wrapper">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slide-wrapper ${index === currentIndex ? 'slide-wrapper-active' : ''}`}
            style={{ backgroundImage: `url(${image})` }}
          />
        ))}
      </div>
    </div>
  );
};

export default ImageSliderr;
