import React, { useState, useEffect, useRef } from 'react';
import './Navbar.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import ImageSlider from './Slider';
import ContactPage from './ContactPage';

const App = () => {
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isSearchVisible, setIsSearchVisible] = useState(false);
  const searchRef = useRef(null); 
  const searchInputRef = useRef(null); 

  const toggleTheme = () => {
    setIsLightTheme(!isLightTheme);
    document.body.classList.toggle('light-theme', !isLightTheme);
  };

  const handleSearchClick = () => {
    setIsSearchVisible(true);
  };

  const handleCloseSearch = () => {
    setIsSearchVisible(false);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchVisible(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  useEffect(() => {
    if (isSearchVisible && searchInputRef.current) {
      searchInputRef.current.focus(); 
    }
  }, [isSearchVisible]);






  return (
    <Router>
      <div className="topprice" style={{ textAlign: 'center' }}>
        <p>FREE shipping on all prepaid orders above Rs. 800</p>
      </div>

      <nav className={`navbar ${isLightTheme ? 'light-theme' : ''}`}>
        {!isSearchVisible ? (
          <>
            <div className="logo">Gshankar!</div>
            <ul className="nav-links">
              <li><Link to="/ContactPage" style={{color:'white',textDecoration:'none',fontSize:'20px'}}>Contact</Link></li>
              <li><Link to="/new-arrivals" >New Arrivals</Link></li>
              <li><Link to="/topwear">Topwear</Link></li>
              <li><Link to="/bottomwear">Bottomwear</Link></li>
              <li><Link to="/loungewear">Loungewear</Link></li>
              <li><Link to="/track-order">Track Order</Link></li>
              <li><Link to="/store-locator">Store Locator</Link></li>
            </ul>
            <div className="nav-icons">
              <span onClick={handleSearchClick} className="search-icon">üîç</span>
              <span>üë§</span>
              <span>üõí</span>
              <button className="theme-switcher" onClick={toggleTheme}>
                {isLightTheme ? 'üåô' : 'üåû'}
              </button>
            </div>
          </>
        ) : (
          <div ref={searchRef} className={`search-container ${isSearchVisible ? 'show' : ''}`}>
            <input
              type="text"
              placeholder="Search..."
              className="search-input"
              ref={searchInputRef} // Attach the ref to the input
            />
            <button className="search-close" onClick={handleCloseSearch}>‚ùå</button>
          </div>
        )}
      </nav>

      <div className="maincontent">
        <Routes>
          <Route path="/" element={<ImageSlider />} />
          <Route path="/ContactPage" element={<ContactPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
