import React, { useState } from 'react';
import './ForgotPasswordModal.css';
import Logo from './images/logo.png';

const ForgotPasswordModal = ({ isOpen, onClose }) => {
    const [email, setEmail] = useState('');
    const [showOtpModal, setShowOtpModal] = useState(false);
    const [otp, setOtp] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [showResetModal, setShowResetModal] = useState(false);
    const [message, setMessage] = useState('');

    const handleSubmit = async () => {
        try {
            const response = await fetch('/api/forgot-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email }),
            });
            const data = await response.text();
            if (response.ok) {
                setMessage('OTP sent to your email');
                setShowOtpModal(true);
            } else {
                setMessage(data);
            }
        } catch (error) {
            console.error('Error sending OTP:', error);
            setMessage('An error occurred. Please try again.');
        }
    };

    const handleVerifyOtp = async () => {
        try {
            const response = await fetch('/api/verify-otp1', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, otp }),
            });
            const data = await response.text();
            if (response.ok) {
                setMessage('OTP verified successfully. Please set a new password.');
                setShowOtpModal(false);
                setShowResetModal(true);
            } else {
                setMessage(data);
            }
        } catch (error) {
            console.error('Error verifying OTP:', error);
            setMessage('An error occurred. Please try again.');
        }
    };

    const handleResetPassword = async () => {
        try {
            const response = await fetch('/api/reset-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email, newPassword }),
            });
            const data = await response.text();
            if (response.ok) {
                setMessage('Password reset successfully. You can now log in.');
                setShowResetModal(false);
                onClose(); // Close the modal after successful reset
            } else {
                setMessage(data);
            }
        } catch (error) {
            console.error('Error resetting password:', error);
            setMessage('An error occurred. Please try again.');
        }
    };

    if (!isOpen) return null;

    return (
        <>
            <div className="modal-overlay">
                <div className="modal-container">
                    <img src={Logo} alt="Logo" className="modal-logo" />
                    <h2 className="modal-title" style={{ fontFamily: 'Twentieth Century sans-serif' }}>Forgot Password</h2>
                    <input 
                        type="email" 
                        placeholder="Enter your email" 
                        className="modal-input" 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <div className="modal-buttons">
                        <button className="modal-button close-button" onClick={onClose} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                            Close
                        </button>
                        
                        <button className="modal-button submit-button" onClick={handleSubmit} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                            Submit
                        </button>
                    </div>
                    {message && <p className="modal-message">{message}</p>}
                </div>
            </div>

            {showOtpModal && (
                <div className="modal-overlay">
                    <div className="modal-container">
                        <h3 className="modal-title" style={{ fontFamily: 'Twentieth Century sans-serif' }}>Enter OTP</h3>
                        <input 
                            type="text" 
                            placeholder="Enter your OTP" 
                            className="modal-input" 
                            value={otp}
                            onChange={(e) => setOtp(e.target.value)}
                        />
                        <div className="modal-buttons">
                            <button className="modal-button close-button" onClick={() => setShowOtpModal(false)} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                                Close
                            </button>
                            
                            <button className="modal-button submit-button" onClick={handleVerifyOtp} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                                Submit
                            </button>
                        </div>
                        {message && <p className="modal-message">{message}</p>}
                    </div>
                </div>
            )}

            {showResetModal && (
                <div className="modal-overlay">
                    <div className="modal-container">
                        <h3 className="modal-title" style={{ fontFamily: 'Twentieth Century sans-serif' }}>Reset Password</h3>
                        <input 
                            type="password" 
                            placeholder="Enter new password" 
                            className="modal-input" 
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                        <div className="modal-buttons">
                            <button className="modal-button close-button" onClick={() => setShowResetModal(false)} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                                Close
                            </button>
                            
                            <button className="modal-button submit-button" onClick={handleResetPassword} style={{ fontFamily: 'Twentieth Century sans-serif' }}>
                                Reset Password
                            </button>
                        </div>
                        {message && <p className="modal-message">{message}</p>}
                    </div>
                </div>
            )}
        </>
    );
};

export default ForgotPasswordModal;
